class FollowButton extends HTMLElement{connectedCallback(){this.addEventListener("click",this.onClick),this.closeButton.addEventListener("click",this.onClose.bind(this))}onClick(){this.followSheetEl.classList.add("show")}onClose(){this.followSheetEl.classList.remove("show")}get followSheetEl(){return this._followSheetEl||(this._followSheetEl=document.querySelector("#follow-sheet")),this._followSheetEl}get closeButton(){return this._closeButton||(this._closeButton=document.querySelector("#follow-sheet-close")),this._closeButton}}customElements.define("follow-button-wc",FollowButton);