class HeroArrow extends HTMLElement{connectedCallback(){this.addEventListener("click",this.onClick)}get direction(){return this.dataset.direction}get heroItemsEl(){return this._heroItemsEl||(this._heroItemsEl=document.querySelector("#hero-items")),this._heroItemsEl}onClick(){"left"===this.direction&&!1===this.heroItemsEl.classList.contains("left-item")&&(!1===this.heroItemsEl.classList.contains("right-item")?this.heroItemsEl.classList.add("left-item"):this.heroItemsEl.classList.remove("right-item")),"right"===this.direction&&!1===this.heroItemsEl.classList.contains("right-item")&&(!1===this.heroItemsEl.classList.contains("left-item")?this.heroItemsEl.classList.add("right-item"):this.heroItemsEl.classList.remove("left-item"))}}customElements.define("hero-arrow-wc",HeroArrow);