import{BasePlayerTemplate}from"./base-player-template.89c77b4e.js";class LeftNavPlayerTemplate extends BasePlayerTemplate{get style(){return'\n      ::slotted([slot="date"]) {\n        color: var(--gray-1);\n        font-size: 10px;\n        font-weight: 800;\n        letter-spacing: 1px;\n        text-transform: uppercase;\n        margin: 16px 0 0 0;\n      }\n      .play-wrapper {\n        display: flex;\n        margin: 16px 0;\n      }\n      .play-button {\n        border-radius: 100%;\n        display: flex;\n        margin: 0 12px 0 0;\n        cursor: pointer;\n        background-color: var(--gray-2);\n        width: 32px;\n        height: 32px;\n        max-width: 32px;\n        max-height: 32px;\n        min-width: 32px;\n        min-height: 32px;\n        background-image: url(/audio/static/images/audio-play.dd3d7339.svg);\n        background-repeat: no-repeat;\n        background-position: 10px center;\n        background-size: 45%;\n      }\n      :host(.loading) .play-button {\n        opacity: .4;\n      }\n      :host(.playing) .play-button {\n        background-image: url(/audio/static/images/audio-pause.e737b4b8.svg);\n        background-position: center center;\n      }\n      ::slotted([slot="description"]) {\n        color: var(--gray-3);\n        font-size: 14px;\n        letter-spacing: 0;\n        line-height: 17.5px;\n      }\n    '}get markup(){return'\n      <slot name="date"></slot>\n      <div class="play-wrapper">\n        <div class="play-button" id="playpause"></div>\n        <slot name="description"></slot>\n      </div>\n    '}}export{LeftNavPlayerTemplate as default};