class PlayerModel{constructor(t){this.audioPlayer=t}get title(){let t="Unknown Title";const e=this.audioPlayer.querySelector('[slot="title"]');return e&&""!==e.innerHTML&&(t=e.innerHTML),t}get author(){let t="Unknown Author";const e=this.audioPlayer.querySelector('[slot="author"]');return e&&""!==e.innerHTML&&(t=e.innerHTML),t}get description(){let t="Unknown Description";const e=this.audioPlayer.querySelector('[slot="description"]');return e&&""!==e.innerHTML&&(t=e.innerHTML),t}get url(){let t=null;const e=this.audioPlayer.getAttribute("src");return e&&(t=e),t}get image(){const t={medium:"default",large:"default",original:"default"},e=this.audioPlayer.querySelector("picture");if(e){e.querySelectorAll("source").forEach((e=>{e.dataset.type&&(t[e.dataset.type]=e.getAttribute("srcset"))}))}return t}get type(){let t="default";return this.audioPlayer.dataset.type&&(t=this.audioPlayer.dataset.type),t}get titleHref(){const t=this.audioPlayer.querySelector('[slot="title"]');if(t){const e=t.getAttribute("href");if(e&&""!==e)return e}return null}get authorHref(){const t=this.audioPlayer.querySelector('[slot="author"]');if(t){const e=t.getAttribute("href");if(e&&""!==e)return e}return null}get guid(){return this.audioPlayer.dataset.guid}get template(){return this.audioPlayer.template}get tracks(){const t=[{position:0,url:this.url,title:this.title,author:this.author,description:this.description,image:this.image,type:this.type,titleHref:this.titleHref,authorHref:this.authorHref,guid:this.guid,template:this.template}];return this.audioPlayer.querySelectorAll("track").forEach(((e,r)=>{t.push({position:r+1,url:e.dataset.src,title:e.dataset.title,author:e.dataset.author,description:e.dataset.description,image:{medium:e.dataset.imagem,large:e.dataset.imagel,original:e.dataset.imageo},type:e.dataset.type,titleHref:e.dataset.titlehref,authorHref:e.dataset.authorhref,guid:e.dataset.guid,template:this.template})})),t}}export{PlayerModel};